import{_ as e,c as i,o as a,a as l}from"./app.fb1780b6.js";const g='{"title":"Changelog","description":"","frontmatter":{"sidebarDepth":2},"headers":[{"level":2,"title":"Version 1.0.0 (2023-06-15)","slug":"version-1-0-0-2023-06-15"},{"level":3,"title":"Major Features","slug":"major-features"},{"level":3,"title":"System Components","slug":"system-components"},{"level":3,"title":"User Interface","slug":"user-interface"},{"level":3,"title":"Known Issues","slug":"known-issues"},{"level":2,"title":"Version 0.9.0 (2023-04-20) - Beta Release","slug":"version-0-9-0-2023-04-20-beta-release"},{"level":3,"title":"Major Features","slug":"major-features-1"},{"level":3,"title":"Improvements","slug":"improvements"},{"level":3,"title":"Bug Fixes","slug":"bug-fixes"},{"level":2,"title":"Version 0.8.0 (2023-03-10) - Alpha Release","slug":"version-0-8-0-2023-03-10-alpha-release"},{"level":3,"title":"Major Features","slug":"major-features-2"},{"level":3,"title":"Known Issues","slug":"known-issues-1"},{"level":2,"title":"Version 0.7.0 (2023-02-05) - Developer Preview","slug":"version-0-7-0-2023-02-05-developer-preview"},{"level":3,"title":"Major Features","slug":"major-features-3"},{"level":3,"title":"Limitations","slug":"limitations"},{"level":2,"title":"Upcoming Features","slug":"upcoming-features"},{"level":3,"title":"Version 1.1.0 (Planned)","slug":"version-1-1-0-planned"},{"level":3,"title":"Version 1.2.0 (Planned)","slug":"version-1-2-0-planned"},{"level":3,"title":"Long-term Roadmap","slug":"long-term-roadmap"},{"level":2,"title":"Release Schedule","slug":"release-schedule"},{"level":2,"title":"Versioning Policy","slug":"versioning-policy"},{"level":2,"title":"Reporting Issues","slug":"reporting-issues"},{"level":2,"title":"Contributing","slug":"contributing"}],"relativePath":"guide/changelog.md","lastUpdated":1746534938333}',r={},n=l('<h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-hidden="true">#</a></h1><p>This page documents the version history of Twoyi, including new features, improvements, bug fixes, and other changes.</p><h2 id="version-1-0-0-2023-06-15" tabindex="-1">Version 1.0.0 (2023-06-15) <a class="header-anchor" href="#version-1-0-0-2023-06-15" aria-hidden="true">#</a></h2><h3 id="major-features" tabindex="-1">Major Features <a class="header-anchor" href="#major-features" aria-hidden="true">#</a></h3><ul><li>Initial public release of Twoyi</li><li>Support for Android 8.1 through 12</li><li>Rootless Android container system</li><li>Fast boot time (approximately 3 seconds after initial setup)</li><li>Built-in file manager</li><li>Application import functionality</li><li>Custom ROM support</li></ul><h3 id="system-components" tabindex="-1">System Components <a class="header-anchor" href="#system-components" aria-hidden="true">#</a></h3><ul><li>Android 8.1 base system</li><li>Customizable system components</li><li>Virtual Hardware Abstraction Layer (HAL)</li><li>Inter-process communication system</li><li>Rendering engine for UI display</li></ul><h3 id="user-interface" tabindex="-1">User Interface <a class="header-anchor" href="#user-interface" aria-hidden="true">#</a></h3><ul><li>Home screen launcher</li><li>Settings interface</li><li>Application management</li><li>File management interface</li><li>System control options (shutdown, reboot)</li></ul><h3 id="known-issues" tabindex="-1">Known Issues <a class="header-anchor" href="#known-issues" aria-hidden="true">#</a></h3><ul><li>Limited support for 32-bit applications</li><li>Some Google services require manual setup</li><li>Occasional performance issues on devices with less than 4GB RAM</li><li>Limited hardware virtualization for certain components</li></ul><h2 id="version-0-9-0-2023-04-20-beta-release" tabindex="-1">Version 0.9.0 (2023-04-20) - Beta Release <a class="header-anchor" href="#version-0-9-0-2023-04-20-beta-release" aria-hidden="true">#</a></h2><h3 id="major-features-1" tabindex="-1">Major Features <a class="header-anchor" href="#major-features-1" aria-hidden="true">#</a></h3><ul><li>Beta release for testing</li><li>Core container functionality</li><li>Basic application support</li><li>File system access</li><li>Preliminary ROM customization</li></ul><h3 id="improvements" tabindex="-1">Improvements <a class="header-anchor" href="#improvements" aria-hidden="true">#</a></h3><ul><li>Performance optimizations for faster boot time</li><li>Reduced memory usage</li><li>Improved application compatibility</li><li>Enhanced file management</li></ul><h3 id="bug-fixes" tabindex="-1">Bug Fixes <a class="header-anchor" href="#bug-fixes" aria-hidden="true">#</a></h3><ul><li>Fixed crash when importing certain applications</li><li>Resolved file permission issues</li><li>Fixed network connectivity problems</li><li>Addressed UI rendering glitches</li></ul><h2 id="version-0-8-0-2023-03-10-alpha-release" tabindex="-1">Version 0.8.0 (2023-03-10) - Alpha Release <a class="header-anchor" href="#version-0-8-0-2023-03-10-alpha-release" aria-hidden="true">#</a></h2><h3 id="major-features-2" tabindex="-1">Major Features <a class="header-anchor" href="#major-features-2" aria-hidden="true">#</a></h3><ul><li>Alpha release for early testing</li><li>Basic container functionality</li><li>Limited application support</li><li>Experimental ROM customization</li></ul><h3 id="known-issues-1" tabindex="-1">Known Issues <a class="header-anchor" href="#known-issues-1" aria-hidden="true">#</a></h3><ul><li>Unstable on some devices</li><li>Limited application compatibility</li><li>Performance issues</li><li>Incomplete documentation</li></ul><h2 id="version-0-7-0-2023-02-05-developer-preview" tabindex="-1">Version 0.7.0 (2023-02-05) - Developer Preview <a class="header-anchor" href="#version-0-7-0-2023-02-05-developer-preview" aria-hidden="true">#</a></h2><h3 id="major-features-3" tabindex="-1">Major Features <a class="header-anchor" href="#major-features-3" aria-hidden="true">#</a></h3><ul><li>Initial developer preview</li><li>Core system architecture</li><li>Basic rendering engine</li><li>Proof-of-concept functionality</li></ul><h3 id="limitations" tabindex="-1">Limitations <a class="header-anchor" href="#limitations" aria-hidden="true">#</a></h3><ul><li>For developer testing only</li><li>Many features incomplete or non-functional</li><li>Significant performance issues</li><li>Limited device compatibility</li></ul><h2 id="upcoming-features" tabindex="-1">Upcoming Features <a class="header-anchor" href="#upcoming-features" aria-hidden="true">#</a></h2><p>The following features are planned for future releases:</p><h3 id="version-1-1-0-planned" tabindex="-1">Version 1.1.0 (Planned) <a class="header-anchor" href="#version-1-1-0-planned" aria-hidden="true">#</a></h3><ul><li>Improved performance and stability</li><li>Enhanced application compatibility</li><li>Better Google services integration</li><li>Additional virtual hardware components</li><li>User interface improvements</li></ul><h3 id="version-1-2-0-planned" tabindex="-1">Version 1.2.0 (Planned) <a class="header-anchor" href="#version-1-2-0-planned" aria-hidden="true">#</a></h3><ul><li>Support for Android 10 as the container OS</li><li>Expanded Xposed framework support</li><li>More comprehensive Magisk module support</li><li>Advanced file management features</li><li>Multi-user enhancements</li></ul><h3 id="long-term-roadmap" tabindex="-1">Long-term Roadmap <a class="header-anchor" href="#long-term-roadmap" aria-hidden="true">#</a></h3><ul><li>Support for newer Android versions</li><li>Enhanced performance optimizations</li><li>Expanded hardware virtualization</li><li>Improved developer tools</li><li>Additional customization options</li></ul><h2 id="release-schedule" tabindex="-1">Release Schedule <a class="header-anchor" href="#release-schedule" aria-hidden="true">#</a></h2><p>Twoyi follows an approximate release schedule:</p><ul><li><strong>Major Releases</strong> (x.0.0): Every 6-12 months</li><li><strong>Feature Releases</strong> (x.y.0): Every 2-3 months</li><li><strong>Bug Fix Releases</strong> (x.y.z): As needed</li></ul><p>Release dates are subject to change based on development progress and priorities.</p><h2 id="versioning-policy" tabindex="-1">Versioning Policy <a class="header-anchor" href="#versioning-policy" aria-hidden="true">#</a></h2><p>Twoyi follows semantic versioning (SemVer) with the format MAJOR.MINOR.PATCH:</p><ul><li><strong>MAJOR</strong>: Incompatible API changes or significant new features</li><li><strong>MINOR</strong>: Backward-compatible new features and improvements</li><li><strong>PATCH</strong>: Backward-compatible bug fixes</li></ul><h2 id="reporting-issues" tabindex="-1">Reporting Issues <a class="header-anchor" href="#reporting-issues" aria-hidden="true">#</a></h2><p>If you encounter issues with any version of Twoyi:</p><ol><li>Check if the issue is already known (listed in the release notes)</li><li>Verify if the issue has been fixed in a newer version</li><li>Report the issue on <a href="https://github.com/twoyi/twoyi/issues" target="_blank" rel="noopener noreferrer">GitHub</a> with: <ul><li>Twoyi version</li><li>Device information</li><li>Steps to reproduce</li><li>Expected vs. actual behavior</li></ul></li></ol><h2 id="contributing" tabindex="-1">Contributing <a class="header-anchor" href="#contributing" aria-hidden="true">#</a></h2><p>We welcome contributions to help improve Twoyi:</p><ul><li>Bug fixes</li><li>Feature implementations</li><li>Documentation improvements</li><li>Performance optimizations</li></ul><p>See the <a href="/guide/contributing.html">Contributing Guide</a> for more information on how to contribute to the project.</p>',50),t=[n];function s(o,d,u,h,c,p){return a(),i("div",null,t)}var f=e(r,[["render",s]]);export{g as __pageData,f as default};
